Insert INTO `Product` (`name` , `brand`, `origin`, `image`, `description`, `category`, `price`, `inventory`, `date02` ) VALUES ('Tẩy trang Bioderma',21,default,1,'Sensibio H2O được bào chế với độ pH sinh lý khoảng 5.5 tương tự như độ pH của làn da, do đó giúp nâng niu sự cân bằng về mặt sinh học - điều cần thiết để duy trì làn da khỏe mạnh. Bên cạnh đó, sản phẩm chứa thành phần nước tinh khiết, đạt chuẩn dược phẩm và một hợp chất bao gồm 3 loại đường mô phỏng sinh học có công dụng làm dịu và ngăn ngừa các phản ứng viêm. Việc lựa chọn cẩn thận các thành phần đảm bảo khả năng dung nạp hoàn hảo và loại bỏ bất kỳ nguy cơ gây tổn thương da nào của sản phẩm.',15,370000,100, Now() );
Insert INTO `Product` (`name` , `brand`, `origin`, `image`, `description`, `category`, `price`, `inventory`, `date02` ) VALUES ('Kem chống nắng Sunplay',23,default,3,'Sunplay là thương hiệu thuộc tập đoàn Rohto-Mentholatum được thành lập từ năm 1899 tại Osaka, Nhật Bản. Qua hơn 100 năm có mặt trên thị trường, đến nay thương hiệu Rohto-Mentholatum đã phát triển và mở rộng việc kinh doanh trên cả 5 châu lục với 11 chi nhánh trên khắp thế giới, sở hữu một loạt thương hiệu con chuyên về lĩnh vực dược mỹ phẩm chăm sóc sức khỏe. ',15,129000,100, Now() );
Insert INTO `Product` (`name` , `brand`, `origin`, `image`, `description`, `category`, `price`, `inventory`, `date02` ) VALUES ('Son lì SHU UEMURA',24,default,4,'Son Lì Shu Uemura Rouge Unlimited Matte 3g từ thương hiệu Shu Uemura của Nhật Bản là dòng son trang điểm môi cao cấp với sự kết hợp hoàn mỹ giữa chất son lì thượng hạng và kết cấu mịn nhẹ như nhung, cho sắc son được bền màu mà vẫn duy trì cảm giác thoải mái, nhẹ nhàng suốt ngày dài.',16,559000,100, Now() );
Insert INTO `Product` (`name` , `brand`, `origin`, `image`, `description`, `category`, `price`, `inventory`, `date02` ) VALUES ('Nước hoa Lancôme',25,default,5,'Nước Hoa Nữ Lancôme La Vie Est Belle EDP là dòng sản phẩm nước hoa eau de parfum cho nữ ra đời trong mùa thu năm 2012 bởi thương hiệu cao cấp Lancôme của Pháp. Khái niệm hương thơm này tập trung ý tưởng về vẻ đẹp tự nhiên và giản dị, tự do từ các công ước và chọn lựa tầm nhìn cho hạnh phúc riêng của bạn. Nước hoa mang một phẩm chất triển vọng về cuộc sống và được lấy cảm hứng từ niềm vui trong những điều nhỏ nhặt.',20,3094000,100, Now() );
Insert INTO `Product` (`name` , `brand`, `origin`, `image`, `description`, `category`, `price`, `inventory`, `date02` ) VALUES ('Bộ gội xả HAIRBURST',26,default,6,'Bộ Dầu Gội & Dầu Xả Hỗ Trợ Mọc Tóc HAIRBURST Avocado & Coconut For Longer Stronger Hair là dòng sản phẩm chăm sóc tóc từ HAIRBURST - thương hiệu chăm sóc tóc nổi tiếng tại Anh, chứa các thành phần thiên nhiên giúp nuôi dưỡng cho mái tóc chắc khỏe sâu từ bên trong, kích thích tóc mọc nhanh và dài hơn, mang đến vẻ quyến rũ bồng bềnh cho mọi mái tóc.',17,569000,100, Now() );
Insert INTO `Product` (`name` , `brand`, `origin`, `image`, `description`, `category`, `price`, `inventory`, `date02` ) VALUES ('Mặt Nạ Tóc Tsubaki',27,'Japan',7,'Mặt Nạ Tóc Cao Cấp Phục Hồi Hư Tổn TSUBAKI Premium Repair Mask đến từ thương hiệu chăm sóc tóc nổi tiếng TSUBAKI Nhật Bản mang đến trải nghiệm dưỡng tóc chuẩn Salon mà không mất thời gian chờ đợi, mái tóc suôn mượt và mềm mại với các thành phần giàu dưỡng chất và công thức phục hồi tiên tiến vượt bậc',17,169000,100, Now() );
